import Head from 'next/head'
import Mainlayout from '../../layout/mainlayout'
import { Banner, Product, About, ContactUs } from '../../component/pages'
// import { db } from "../../firebaseConfig";


import firebase from "firebase/app"
import "firebase/auth"
import "firebase/storage"
import 'firebase/database';
import 'firebase/firestore';
const app = firebase.initializeApp({
  apiKey: "AIzaSyC2GIk1pnAtTdDgn-UiqIDW3y4EGy448pg",
  authDomain: "nrs-cms.firebaseapp.com",
  databaseURL: "https://nrs-cms-default-rtdb.firebaseio.com",
  projectId: "nrs-cms",
  storageBucket: "nrs-cms.appspot.com",
  messagingSenderId: "94826903466",
  appId: "1:94826903466:web:24fe51cce92e1cb2e39dd3"
})
export const db = firebase.firestore();
export default class Home extends React.Component {


  state = {
    bannerImg: '',


  }




  componentDidMount() {

    db.collection("banner").doc("001").get().then((docRef) => {
      let { b_img } = docRef.data();
      this.setState({ bannerImg: b_img })

    }).catch((error) => { })
  }
  render() {
    let { bannerImg} = this.state;
    return (
      <div >
        <Head>
          <title>NRS CHICKEN</title>
          <link rel="icon" href="/favicon.ico" />
        </Head>
        <Mainlayout>
          <Banner bannerImg={bannerImg}/>
          <Product />
          <ContactUs />
          <About />

        </Mainlayout>
      </div>
    )
  }
}
