import Head from 'next/head'
import Mainlayout from '../../layout/mainlayout'
import { Banner, Product, About, ContactUs } from '../../component/pages'
import { db } from "./firebaseConfig";




export default class Home extends React.Component {


  state = {
    bannerImg: '',
    productList: [],
    productLoder: true,
    about: {
      createDate: '',
      text: '',
      updatedDate: ''
  },
  }




  componentDidMount() {

    db.collection("banner",).doc("001").get().then((docRef) => {
      let { b_img } = docRef.data();
      this.setState({ bannerImg: b_img })
    }).catch((error) => { })

    db.collection('product').orderBy("name", "asc").get().then((querySnapshot) => {
      let data = querySnapshot.docs.map(doc => {
        console.log('LOG 1', doc.data());

        return { id: doc.id, ...doc.data() }
      });
      console.log('LOG 2', data);
      this.setState({ productList: data, productLoder: false })
    }).catch((error) => { this.setState({ productLoder: false }) })


    db.collection("about").doc("001").get().then((docRef) => {

      this.setState({ about: docRef.data(),isLoder:false })
      // console.log(docRef.data())
  }).catch((error) => { this.setState({isLoder:false})})

  }
  render() {
    let { bannerImg, productList,productLoder } = this.state;
    return (
      <div >
        <Head>
          <title>NRS CHICKEN</title>
          <link rel="icon" href="/favicon.ico" />
        </Head>
        <Mainlayout>
          <Banner bannerImg={bannerImg} />
          <Product productList={productList} productLoder={productLoder}/>
          <ContactUs />
          <About about={about}/>

        </Mainlayout>
      </div>
    )
  }
}
