// const mailer = Mailer(mailerConfig, emailsList)
export default class Home extends React.Component {


  state = {
    bannerImg: '',
    productList: [],
    productLoder: true,
    about: {
      createDate: '',
      text: '',
      updatedDate: ''
    },
    allPromos: []
  }




  componentDidMount() {
    let { allPromos } = this.state;
    db.collection("banner",).doc("001").get().then((docRef) => {
      let { b_img } = docRef.data();
      allPromos.push('1');
      this.setState({ bannerImg: b_img, allPromos })
    }).catch((error) => { })

    db.collection('product').orderBy("name", "asc").get().then((querySnapshot) => {
      let data = querySnapshot.docs.map(doc => {
        console.log('LOG 1', doc.data());

        return { id: doc.id, ...doc.data() }
      });
      console.log('LOG 2', data);
      allPromos.push('2');
      this.setState({ productList: data, productLoder: false, allPromos })
    }).catch((error) => { this.setState({ productLoder: false }) })


    db.collection("about").doc("001").get().then((docRef) => {
      allPromos.push('3')
      this.setState({ about: docRef.data(), isLoder: false, allPromos })
      // console.log(docRef.data())
    }).catch((error) => { this.setState({ isLoder: false }) })
    // this.sendMail()
  }



  // sendMail = () => {
  //   mailer.send('pass', {
  //     firstName: 'Mathieu',
  //     lastName: 'Tudisco',
  //     brand: 'Linkvalue',
  //     newAccount: true,
  //     password: Math.random().toString(36).substring(7),
  //   }, {
  //     to: 'kb.anvesh1996@gmial.com',
  //     attachments: [{ content: 'bar', filename: 'foo.txt' }],
  //   })

  // }




  render() {
    let { bannerImg, productList, productLoder, about, allPromos } = this.state;
    return (
      <div >
        <Head>
          <title>NRS CHICKEN</title>
          <link rel="icon" href="/favicon.ico" />
          <script src="https://smtpjs.com/v3/smtp.js"></script>
        </Head>
        <Mainlayout headerLoder={!(allPromos.length !== 3)}>
          {allPromos.length === 3 ?
            <>
              <Banner bannerImg={bannerImg} />
              <Product productList={productList} productLoder={productLoder} />
              <ContactUs />
              <About about={about} />
            </> : ""}
        </Mainlayout>
      </div>
    )
  }
}
